import {useNavigate} from 'react-router-dom';


const signup = () => {

  const[credentials,setCredentials] = useState({name:"",email:"",password:"" ,cpassword:""});
  const navigate = useNavigate();


  const handleSubmit = async(e)=>{
    e.preventDefault(); 
    const response = await fetch("http://localhost:5000/api/auth/createUser", {
       method: "POST", 
       headers: {
         "Content-Type": "application/json",
         "auth-token" : "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjYzZDMyODQ1M2ZmYzY2MTUzYWFkNGE1In0sImlhdCI6MTcxNTMxNzU0N30.Y8ZdecVTjLQYCaS-wz_fyLEmtOAzvnX2IEvYddP6irE",
       },
       body: JSON.stringify({email:credentials.email , password:credentials.password})
     });
     const json = await response.json(); 
     console.log(json); 
      if(json.success)
       {
           //save the auth token and redirect
           localStorage.setItem('token',json.authtoken);
           navigate('/');
       }
       else{
           alert("Invalid Credentials");
       }
   }
   const onChange =(e)=>{
       setCredentials({...credentials,[e.target.name]:e.target.value })
  return (
  <div className='container'>
    <form onSubmit={handleSubmit}>
    <div className="mb-3">
    <label htmlFor="name" className="form-label">Enter Your Name</label>
    <input type="text" className="form-control" id="name" name="name" aria-describedby="emailHelp" onChange={onChange}/>
    </div>
    <div className="mb-3">
    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="email" name='email' aria-describedby="emailHelp" onChange={onChange}/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
    </div>
     <div className="mb-3">
    <label htmlFor="password" className="form-label">Password</label>
    <input type="password" className="form-control" id="password"  onChange={onChange}/>
    </div>
    <div className="mb-3">
    <label htmlFor="cpassword" className="form-label">Confirm Password</label>
    <input type="password" className="form-control" id="cpassword" name="cpassword" onChange={onChange}/>
