import React , {useContext, useState} from 'react'
import noteContext from '../context/notes/noteContext';
const AddNote = () => {
    const context = useContext(noteContext);
    const{addNote} = context;
    const[note , setNote] = useState({title:"" , description:"" ,tag:"default"});
    const HandleClick = (e)=>{
        e.preventDefault();
        addNote(note.title , note.description , note.tag);
    }
    const onChange =(e)=>{
        setNote({...note,[e.target.name]:e.target.value })
    }
  return (
    <div className="container my-3">
      <h1>Add Your Notes</h1>
  <form className='my-3'>
   <div className="form-group">
    <label htmlFor="title">Title</label>
    <input type="email" className="form-control" id="title" name="title" aria-describedby="emailHelp" placeholder="Enter title"onChange={onChange}/>
  </div>
  <div className="form-group">
    <label htmlFor="description">Description</label>
    <input type="text" className="form-control" id="description" name = "description" placeholder="Enter description" onChange={onChange}/>
  </div>
  <button type="submit" className="btn btn-primary" onClick={HandleClick}>Add Note</button>
  </form>
  </div>
  )
}

export default AddNote
