import React ,{useContext, useEffect , useRef , useState} from 'react'
import noteContext from '../context/notes/noteContext';
import AddNote from './AddNote';
import Noteitem from './Noteitem';

const Notes = () => {

    const context = useContext(noteContext);
    const{notes , getNotes} = context;

    useEffect(() => {
      getNotes()
      // eslint-disable-next-line
  }, [])

  const ref = useRef(null)
  const[note , setNote] = useState({title:"" , description:"" ,tag:" "});

  const updateNote = (note) =>{
    console.log("hello");
    ref.current.click();
  }
  const HandleClick = (e)=>{
    e.preventDefault();
    addNote(note.title , note.description , note.tag);
}
  const onChange =(e)=>{
    setNote({...note,[e.target.name]:e.target.value })
 }



  return (
     <>
     <AddNote/>
    
<button  ref={ref} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
  Launch demo modal
</button>
<div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
    <div className="modal-body">

      <form className='my-3'>
      <div className="form-group">
      <label htmlFor="title">Title</label>
      <input type="text" className="form-control" id="etitle" name="etitle" aria-describedby="emailHelp" placeholder="Enter title"onChange={onChange}/>
     </div>
    <div className="form-group">
    <label htmlFor="description">Description</label>
    <input type="text" className="form-control" id="edescription" name = "edescription" placeholder="Enter description" onChange={onChange}/>
    </div>
    <div className="form-group">
    <label htmlFor="description">Tag</label>
    <input type="text" className="form-control" id="etag" name = "etag" placeholder="Enter tag" onChange={onChange}/>
    </div>
  </form>

    </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Update Notes</button>
      </div>
    </div>
  </div>
</div>

     <h1>Your Notes</h1>
     <div className='row my-3'>
     {notes.map((note)=>{
         return <Noteitem key={note._id}  updateNote={updateNote} note={note}/>  
       })}
     </div>

    </>   
  )
}

export default Notes
