import React ,{useContext, useEffect , useRef , useState} from 'react'
import noteContext from '../context/notes/noteContext';
import AddNote from './AddNote';
import Noteitem from './Noteitem';

const Notes = () => {

    const context = useContext(noteContext);
    const{notes , getNotes} = context;
    useEffect(()=>{
      getNotes()
      // eslint-disable-next-line 

    } ,[])

    const updateNote = (note)=>{
      ref.current.click();
    }
    const ref = useRef(null);
    const[note , setNote] = useState({title:"" , description:"" ,tag:" "});

    const HandleClick = (e)=>{
      e.preventDefault();
  }
  const onChange =(e)=>{
      setNote({...note,[e.target.name]:e.target.value })
  }

  return (
     <>
     <AddNote/>
     <button ref={ref} type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
      Launch demo modal
     </button>
    <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"  aria-hidden="true">
    <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Edit Note</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
    <form className='my-3'>
    <div className="form-group">
    <label htmlFor="title">Title</label>
    <input type="email" className="form-control" id="etitle" name="etitle" aria-describedby="emailHelp" placeholder="Enter title"onChange={onChange}/>
    </div>
     <div className="form-group">
    <label htmlFor="description">Description</label>
    <input type="text" className="form-control" id="edescription" name = "edescription" placeholder="Enter description" onChange={onChange}/>
    </div>
   <div className="form-group">
    <label htmlFor="description">Tag</label>
    <input type="text" className="form-control" id="etag" name = "etag" placeholder="Enter tag" onChange={onChange}/>
   </div>
    </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Update Notes</button>
      </div>
    </div>
    </div>
    </div> 
     <h1>Your Notes</h1>
     <div className='row my-3'>

     {notes.map((note)=> {

      return <Noteitem key = {note._id} updateNote={updateNote} note = {note}/>

     })}

     </div>

    </>   
  )
}

export default Notes
